import React from 'react';

const AboutUsSection = () => {
	return (<div className="centred-container">
		<h3>Alex Makes Meals</h3>
		<p>Alex Makes Meals is preparing, cooking and delivering free meals for frontline medical staff, straight to their door!</p>
		<p>As COVID-19 continues, healthcare professionals are working harder than anyone should. The last thing they should be thinking about is where their next meal is coming from.</p>
		<p>We prepare large volumes of food and deliver it to the door to healthcare staff during the ongoing pandemic. All money raised through our GoFundMe campaign allows us to purchase produce and prepare nutritious meals.</p>
		<p>Our entire team are volunteers â€“ our chefs, delivery team, support resources and marketing and communications teams. If you would like to join us in helping the community, please reach out!</p>
	</div>)
}

const About = () => {
	return (<>
		<section className="plain-bg"><AboutUsSection /></section>
		<section className="alternate-bg-2"></section>
	</>)
}


export default About;
