import styled from 'styled-components';

export const SectionContainer = styled.span`
    padding-left: 1em;
    height: 28em;
`

export const SectionHeadingContainer = styled.div`
    display: flex;
    justify-content: center;
    padding-bottom: 2em;
`

export const StatsContainer = styled.span`
    padding-top: 1em;
    padding-bottom: 4em;
    display: flex;
    justify-content: center;
    width: 100%;
`

export const StatsColumn = styled.span`
    text-align: center;
    width: 33%;
`
export const DonationButtonContainer = styled.span`
    padding-top: 2em;
    display: flex;
    justify-content: flex-end;
`

export const DonationButton = styled.div`
    background-color: #7AC143;
    text-align: center;
    width: 30%;
    border-radius: 8px;
    padding-top: 0.4em;
    padding-bottom: 0.4em;
`

export const ButtonText = styled.p`
    font-size:1.5vw;
    color: white;
    line-height: 0;
`
